bend_a.fs.glsl:
precision lowp float;
uniform lowp sampler2D _Tex;
uniform lowp vec4 _Colr;

//varying-----------------------------
varying mediump vec2 _Tex_uv_TEXCOORD0;

//------------------------------------

//texture2DEtC1Mark
#ifdef FOG
uniform lowp vec4 glstate_fog_color; 
varying lowp float factor;
#endif


void main() 
{
    lowp vec4 emission = vec4(.0,.0,.0,.0);

    //----------------------------------------------------------
    lowp vec4 tex = texture2D(_Tex, _Tex_uv_TEXCOORD0);
    lowp vec3 finalColor = _Colr.a * (_Colr.rgb * tex.rgb * 2.0 );
    emission = vec4(finalColor.rgb , (tex.a * _Colr.a));

    #ifdef FOG
        emission.xyz = mix(glstate_fog_color.rgb, emission.rgb, factor);
    #endif
    
    gl_FragData[0] = emission;
}

bend_a.vs.glsl:
attribute highp vec4 _glesVertex;
attribute mediump vec2 _glesMultiTexCoord0;

uniform highp mat4 glstate_matrix_mvp;
uniform mediump vec4 _Tex_ST;

#ifdef FOG
// #define glstate_fog_end		150.
// #define glstate_fog_start	1.
uniform lowp float glstate_fog_start;
uniform lowp float glstate_fog_end;
varying lowp float factor;
#endif

varying mediump vec2 _Tex_uv_TEXCOORD0;

void main()
{
    _Tex_uv_TEXCOORD0 = _glesMultiTexCoord0.xy * _Tex_ST.xy + _Tex_ST.zw;
    highp vec4 position=vec4(_glesVertex.xyz,1.0);

    //----------------------------------------------------------

    position = (glstate_matrix_mvp * position);

    #ifdef FOG
    factor = (glstate_fog_end - abs(position.z))/(glstate_fog_end - glstate_fog_start);
    factor = clamp(factor, 0.0, 1.0);
    #endif


    gl_Position =position;
}

